function openTemplate(templateId) {
    window.location.href = `template.html?template=${templateId}`;
}

function loadTemplate() {
    const urlParams = new URLSearchParams(window.location.search);
    const templateId = urlParams.get('template');
    
    if (templateId && templates[templateId]) {
        document.getElementById('template-title').textContent = templates[templateId].title;
        document.getElementById('template-content').innerHTML = templates[templateId].content;
    } else {
        document.getElementById('template-content').innerHTML = '<p>未找到模板</p>';
    }
}

function printTemplate() {
    window.print();
}

function clearForm() {
    const inputs = document.querySelectorAll('.editable-input, .editable-textarea');
    inputs.forEach(input => {
        if (input.type === 'checkbox') {
            input.checked = false;
        } else {
            input.value = '';
        }
    });
}

function goBack() {
    window.location.href = 'index.html';
}

function initCategoryTabs() {
    const tabBtns = document.querySelectorAll('.tab-btn');
    const templateCards = document.querySelectorAll('.template-card');
    
    tabBtns.forEach(btn => {
        btn.addEventListener('click', function() {
            const category = this.getAttribute('data-category');
            
            tabBtns.forEach(b => b.classList.remove('active'));
            this.classList.add('active');
            
            templateCards.forEach(card => {
                const cardCategory = card.getAttribute('data-category');
                if (category === 'all' || cardCategory === category) {
                    card.style.display = 'block';
                } else {
                    card.style.display = 'none';
                }
            });
        });
    });
}

document.addEventListener('DOMContentLoaded', function() {
    if (document.getElementById('template-title')) {
        loadTemplate();
    }
    if (document.querySelector('.category-tabs')) {
        initCategoryTabs();
    }
});

document.addEventListener('keydown', function(event) {
    if (event.ctrlKey || event.metaKey) {
        if (event.key === 'p') {
            event.preventDefault();
            printTemplate();
        } else if (event.key === 'l') {
            event.preventDefault();
            clearForm();
        }
    }
    
    if (event.key === 'Escape') {
        goBack();
    }
});